INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'P.O.V.', N'pov', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A monthly music and culture magazine based in New York City, covering alternative and independent artists with an irreverent and edgy tone.}}' + CHAR(13) + CHAR(10) + N'P.O.V. (Point of View) was a monthly music and culture magazine based in New York City. It was founded in 1992 by Amy Haben and Michael Goldberg, both former writers for Rolling Stone and other music publications.{{Image image:magazine-cover-not-available.png|Type=PrimaryArticleImage}}' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine covered a wide range of music genres, with a focus on rock, indie, and alternative music. In addition to music coverage, P.O.V. also featured articles on film, literature, and popular culture, as well as interviews with musicians, actors, and other artists.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'P.O.V. was known for its irreverent and edgy tone, as well as its commitment to covering underground and independent artists. Among the writers who contributed to the magazine were several prominent music journalists, including Lester Bangs, Neil Strauss, and Dave Eggers.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Despite its loyal readership and critical acclaim, P.O.V. faced financial difficulties in the late 1990s, due to declining advertising revenue and higher printing costs. The magazine ceased publication in 2000, after eight years in operation.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, P.O.V. is remembered as a pioneering voice in the world of music journalism, having helped to define the alternative and indie music scenes of the 1990s and beyond. Its legacy continues to influence the contemporary landscape of music and culture journalism.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Pacific Marine Review
INSERT Articles (Title, UrlSlug, SiteId, Culture)
