INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A now defunct monthly magazine in the United States that covered collectible card games, such as Magic: The Gathering and other tabletop games, published by Wizard Entertainment.}}' + CHAR(13) + CHAR(10) + N'## Introduction{{Image image:magazine-cover-not-available.png|Type=PrimaryArticleImage}}' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InQuest Gamer was a monthly magazine in the United States that covered collectible card games, such as Magic: The Gathering and other tabletop games. The magazine was published by Wizard Entertainment and was in circulation from 1995 to 2007.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InQuest Gamer was first published in 1995 as a monthly magazine dedicated to collectible card games. It was published by Wizard Entertainment, which was known for its publication of Wizard magazine, a monthly magazine focused on comic book culture.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine quickly became popular among collectors of collectible card games, covering topics such as the latest expansion sets, strategy guides, and reviews of individual cards.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The content of InQuest Gamer was divided into several sections, including "Reviews," "Previews," "Price Guide," "Strategy," and "Collector''s Workshop." The "Reviews" section provided reviews of individual card sets, while the "Previews" section gave an exclusive look at upcoming sets.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The "Price Guide" section was a comprehensive resource for collectors, providing current pricing information for individual cards. The "Strategy" section featured articles on various aspects of collectible card games, while the "Collector''s Workshop" section provided tips for players interested in customizing their cards.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Audience and Reception' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InQuest Gamer had a dedicated readership among players of collectible card games, with a circulation of approximately 150,000 copies at its peak. The magazine was known for its in-depth reviews of individual cards and its comprehensive price guide, which helped collectors understand the value of their collections.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Over the years, InQuest Gamer received several awards for its content, including the Origins Award for "Best Periodical" in 2000 and the Inquest Fan Award for "Best Resource of the year" in 2004.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Legacy' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InQuest Gamer played an important role in the collectible card game community during the late 1990s and early 2000s. The magazine''s extensive coverage of individual cards and its comprehensive price guide helped collectors understand the value of their collections and make informed purchasing decisions.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, InQuest Gamer is remembered as an important resource for the collectible card game community. Its legacy lives on through various online resources and collector communities, which continue to discuss and analyze the value of individual cards.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Inside Kung Fu
INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Inside Kung Fu', N'inside-kung-fu', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A defunct monthly martial arts magazine from the US, covering various topics such as style profiles and training techniques, popularizing kung fu in the 1970s and 1980s.}}' + CHAR(13) + CHAR(10) + N'## Introduction{{Image image:magazine-cover-not-available.png|Type=PrimaryArticleImage}}' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Kung Fu was a monthly magazine in the United States that covered various topics related to the martial arts, including style profiles, training techniques, and interviews with prominent martial artists. The magazine was in circulation from 1973 to 2009.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Kung Fu was first published in 1973 by Rainbow Publications, which was also known for its publication of Black Belt magazine, another martial arts-focused magazine. During the 1970s and 1980s, Inside Kung Fu played an important role in popularizing the martial arts in the United States, particularly kung fu.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine featured style profiles, interviews with prominent martial artists, and training techniques for students of all levels. Over the years, Inside Kung Fu became a popular resource for practitioners of the martial arts.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The content of Inside Kung Fu was divided into several sections, including "Features," "Training," "Interviews," and "News." The "Features" section focused on various aspects of the martial arts, including the history and philosophy of various styles.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The "Training" section provided readers with techniques for improving their skills in various areas of the martial arts, including forms, sparring, and weapons training. The "Interviews" section featured interviews with prominent martial artists, including Bruce Lee and Jackie Chan.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The "News" section provided readers with information about upcoming martial arts events and competitions, as well as news about the development of the martial arts in various countries.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Audience and Reception' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Kung Fu had a dedicated readership among practitioners of the martial arts, with a circulation of approximately 100,000 copies at its peak. The magazine was known for its in-depth coverage of various martial arts styles, as well as its comprehensive training tips.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Over the years, Inside Kung Fu received several awards for its content, including the Black Belt Hall of Fame Award for "Magazine of the Year" in 1993 and 1998.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Legacy' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Kung Fu played an important role in the popularization of the martial arts in the United States during the 1970s and 1980s. The magazine''s in-depth coverage of various styles and its comprehensive training tips helped practitioners improve their skills and understand the broader context of the martial arts.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, Inside Kung Fu is remembered as an important resource for practitioners of the martial arts, particularly kung fu. Its legacy lives on through various online resources and martial arts communities, which continue to discuss and analyze the techniques and philosophies.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Inside Sports
INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Inside Sports', N'inside-sports', 1, 'en' );
