INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Television/Radio Age', N'television-radio-age', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A monthly magazine covering the broadcasting industry in the United States from 1943-1992.}}' + CHAR(13) + CHAR(10) + N'Television/Radio Age was a monthly magazine that focused on the broadcasting industry in the United States. The magazine was published from 1943 until 1992.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Television/Radio Age was first published in 1943 by Television Magazine Corporation, which was owned by the popular broadcaster Lowell Thomas. The magazine was created as a way to cover the emerging broadcasting industry, which was rapidly growing in popularity as radio and television became more widespread.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Over the years, Television/Radio Age became one of the leading magazines in the broadcasting industry, with a readership that included executives, producers, and advertisers. The magazine was known for its insightful coverage and analysis of the latest trends and developments in broadcasting, covering topics such as programming, advertising, and industry news.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'During the 1970s and 1980s, Television/Radio Age faced increasing competition from other publications, as well as from new media such as cable television and the internet. Despite these challenges, the magazine continued to be an important voice in the broadcasting industry for nearly 50 years.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content and Features' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Television/Radio Age covered a wide variety of topics related to the broadcasting industry. Each issue included news and analysis of the latest trends and developments in television and radio programming, as well as articles on topics such as advertising, ratings, and industry news.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In addition, the magazine featured regular columns on topics such as technology, cable television, and satellite broadcasting. The magazine also included interviews with notable broadcasters and industry executives, making it a valuable resource for anyone interested in the broadcasting industry.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Impact and Legacy' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Television/Radio Age played an important role in the history of the broadcasting industry in the United States. The magazine was instrumental in covering the emergence of television as a dominant medium in American culture, and its coverage of the advertising industry helped to shape the way companies marketed their products on television and radio.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, the legacy of Television/Radio Age can be seen in the many publications that continue to cover the broadcasting industry, as well as in the ongoing growth and development of new media such as cable television and the internet.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Tennis Week
INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Tennis Week', N'tennis-week', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A weekly magazine covering all aspects of the sport of tennis for over three decades.}}' + CHAR(13) + CHAR(10) + N'Tennis Week was a weekly magazine that focused on tennis news, analysis, and interviews with players and coaches. The magazine was published from 1969 until 2001.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Tennis Week was first published in 1969 by Eugene L. Scott, a former editor of the Harvard Crimson and a tennis player himself. The magazine was created as a way to cover the rapidly growing sport of tennis, which was experiencing a surge in popularity in the United States.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Over the years, Tennis Week became one of the leading magazines in the tennis industry, with a readership that included players, coaches, and fans of the sport. The magazine was known for its insightful coverage and analysis of the latest events in the world of tennis, as well as its in-depth interviews with players and coaches.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'During the 1990s, Tennis Week faced increasing competition from other publications as well as from new media such as the internet. Despite these challenges, the magazine continued to publish for nearly 30 years.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content and Features' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Tennis Week covered a wide variety of topics related to tennis, including coverage of major tournaments such as Wimbledon, the U.S. Open, and the French Open. In addition, the magazine included analysis of players and their performance both on and off the court.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Tennis Week also featured regular columns on topics such as equipment, coaching, and fitness for tennis players. The magazine was a valuable resource for anyone interested in learning more about the sport of tennis.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Impact and Legacy' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Tennis Week played an important role in the history of tennis journalism. The magazine was instrumental in covering the rise of the sport of tennis as a major global phenomenon, and its coverage of major tournaments helped to popularize the sport among fans.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, the legacy of Tennis Week can be seen in the many publications that continue to cover the sport of tennis, as well as in the ongoing growth and development of the sport itself.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

