INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Travel Holiday', N'travel-holiday', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A travel magazine providing resources for destinations, lifestyle, cultures, and travel-related products. Published from 1901-2003 by Meredith Corporation.}}' + CHAR(13) + CHAR(10) + N'Travel Holiday was a magazine that focused on travel-related topics and was published by the Meredith Corporation. The magazine was first published in 1901 and continued publication until 2003.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Travel Holiday was founded in 1901 in Chicago, Illinois, United States, as The Four-Track News. The magazine was created as a publication aimed at passengers on the Chicago, Milwaukee and St. Paul Railway, and was distributed free of charge.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine was later renamed Travel, and then Travel Holiday in 1956. In 1997, Meredith Corporation acquired Travel Holiday from Hachette Filipacchi Media U.S., and the magazine became part of Meredith''s National Media Group.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Travel Holiday was a comprehensive resource for travel-related content, including reviews of destinations and travel-related products, travel guides, and vacation planning advice. The magazine also included features on lifestyle and cultural topics related to travel, as well as profiles of interesting people and places from around the world.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'One of the magazine''s most popular features was its annual Travel Holiday Awards, which recognized excellence in the travel industry across a wide range of categories, including hotels, airlines, and destinations.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Impact' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Travel Holiday played an important role in the travel industry, providing travelers with a valuable resource for information and inspiration. The magazine helped to promote travel to a wider audience and encouraged readers to explore new destinations and experiences.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The Travel Holiday Awards were also highly regarded within the travel industry, and winning a Travel Holiday Award was considered a prestigious honor.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Cessation of publication' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In 2003, Meredith Corporation announced that it was ceasing publication of Travel Holiday due to declining advertising revenue caused by the rise of the internet. Some of the magazine''s content was merged into other Meredith publications, including Better Homes and Gardens and Parents.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Conclusion' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Travel Holiday was a magazine that focused on travel-related topics and was published by the Meredith Corporation. The magazine was first published in 1901 and continued publication until 2003. Travel Holiday was a comprehensive resource for travel-related content and played an important role in promoting travel to a wider audience. In 2003, the magazine ceased publication due to declining advertising revenue caused by the rise of the internet. Some of the magazine''s content was merged into other Meredith publications.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

