INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A monthly photography magazine published in the United States from 1898 to 1927, targeting amateur photographers and featuring works from notable contributors like Weston, Stieglitz, and Adams.}}' + CHAR(13) + CHAR(10) + N'**Photo-Era** was a monthly photography magazine published in the United States from 1898 until 1927. Initially named American Amateur Photographer and then American Photography, the magazine was renamed Photo-Era in 1911.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The publication was aimed at amateur photographers and covered various aspects of photography, including techniques, equipment, and the work of photographers. Notable contributors to the magazine included Edward Weston, Alfred Stieglitz, and Ansel Adams.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Photo-Era also organized an annual photography contest that was widely regarded as one of the most prestigious amateur photography competitions in the United States. The contest attracted thousands of entries each year and awarded cash prizes and medals to the top winners.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In addition to the magazine, Photo-Era also published a number of photography books and manuals aimed at amateur photographers. The company was eventually acquired by the New York Institute of Photography, and the magazine ceased publication in 1927.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, back issues of Photo-Era are highly collectible among photography enthusiasts and historians, providing a valuable window into the history of amateur photography in the early 20th century.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Photoplay
INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Photoplay', N'photoplay', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A popular American film magazine published from 1911 to 1980 known for its coverage of the film industry and exclusive photos of Hollywood stars.}}' + CHAR(13) + CHAR(10) + N'**Photoplay** was a popular American film magazine published from 1911 to 1980. Originally started as a motion picture fan magazine, Photoplay became one of the most influential and widely circulated movie magazines in the United States.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine covered various aspects of the film industry including news, interviews, reviews, and feature articles on actors and filmmakers. Photoplay also published exclusive and often controversial photos of Hollywood stars, frequently sparking rumors and gossip about their private lives.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'During the 1920s and 1930s, Photoplay routinely published reader polls for selecting the most popular actors and actresses, known as the "Photoplay Medal Awards". The awards were highly coveted by Hollywood stars and helped to cultivate public interest and fan engagement in the film industry.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In the 1940s, Photoplay shifted its focus from movie news to romantic fiction, publishing serialized stories with headshots of actors on the cover. The magazine''s popularity continued to decline in the following decades and it eventually ceased publication in 1980.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Despite its controversial reputation, Photoplay is remembered not only for its contributions to popular celebrity culture, but also as a valuable historical resource for understanding the evolution of Hollywood and the film industry in the 20th century.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

