INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Popular Science', N'popular-science', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A magazine focused on science and technology, known for in-depth coverage of scientific innovations, product reviews, and advice on improving everyday life.}}' + CHAR(13) + CHAR(10) + N'**Popular Science** is a magazine that focuses on science and technology. The magazine was first published in 1872 under the title "The Popular Science Monthly," and was renamed Popular Science in 1915.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Popular Science provided readers with in-depth coverage of scientific and technological innovations, with features on topics such as space exploration, environmental issues, robotics, and health. The magazine also offered reviews of new products and technologies, as well as advice on how to improve a reader''s everyday life.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Popular Science was published by a number of different companies over the years, including the Bonnier Corporation, Time, Inc., and the Popular Science Publishing Company. The magazine had a loyal following among science enthusiasts and other readers interested in learning about new scientific discoveries and innovations.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In 2020, Bonnier Corporation announced that it would be ceasing publication of the print edition of Popular Science in order to focus on digital content. The final print issue of the magazine was published in January 2021, with the magazine continuing to be available online.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Despite the end of its print edition, Popular Science remains an influential and widely read publication on science and technology. Its archives contain a wealth of information on scientific advancements of the past century and serve as an important historical record of the field.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Portfolio
INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Portfolio', N'portfolio', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A former American magazine focused on business, finance, and investment news that ceased publication in 2009 due to declining advertising revenue.}}' + CHAR(13) + CHAR(10) + N'**Portfolio** was an American magazine that focused on business, finance, and investment news. The magazine was first published in April 2007, and was owned by Condé Nast Publications.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Portfolio aimed to provide its readers with an inside look into the world of business and finance, with features on corporate leaders, Wall Street players, and economic trends. The magazine also covered topics such as real estate, media, and international business.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Although Portfolio was well-regarded by critics for its high-quality journalism, the magazine struggled during the financial crisis of the late 2000s. Advertisers pulled out of the magazine in droves, and circulation declined significantly.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In April 2009, Condé Nast announced that it would be ceasing publication of Portfolio, citing the difficult economic environment. The magazine''s last issue was published in May 2009.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Despite its short run, Portfolio had a significant impact on business journalism and helped usher in a new era of reporting on Wall Street and the world of finance. Many of the magazine''s top reporters and editors went on to successful careers at other publications. The archives of the magazine remain an important resource for historians and researchers studying the financial crisis and its aftermath.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

