INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Inside Sports', N'inside-sports', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A defunct monthly sports magazine from the US that focused on long-form journalism and in-depth coverage of various sports, but later shifted to more mainstream content in the 1990s.}}' + CHAR(13) + CHAR(10) + N'## Introduction' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Sports was a monthly sports magazine in the United States that covered various sports, including basketball, football, baseball, and boxing. The magazine served as a competitor to popular sports magazines, such as Sports Illustrated and ESPN The Magazine. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Sports was first published in 1980 by editor Harold Rosenthal and publisher Joe Concannon. The magazine was published by News America Publishing Inc., a subsidiary of News Corporation. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In its early years, the magazine focused on long-form journalism, featuring in-depth stories and profiles of prominent athletes. However, in the 1990s, Inside Sports shifted its focus to more mainstream and celebrity-driven content. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine ceased publication in 1998, due to financial troubles and declining readership. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Sports covered a range of sports, including basketball, football, baseball, boxing, and golf, among others. The magazine featured coverage of professional and amateur sports, as well as human interest stories and profiles of famous athletes. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In addition to its sports coverage, the magazine was also known for its photography, which was often featured prominently on its covers and inside pages. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Audience and Reception' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Inside Sports had a dedicated readership among sports fans and enthusiasts. The magazine was known for its focus on long-form journalism and its in-depth coverage of athletes and sports. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'However, some criticized the magazine for its shift to more celebrity-driven and mainstream content in the 1990s. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Legacy' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Despite its relatively short publication history, Inside Sports is remembered as an influential sports magazine that helped shape the landscape of sports journalism in the United States. Its focus on in-depth coverage and long-form journalism influenced other sports publications, such as Sports Illustrated and ESPN The Magazine. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, fans of the magazine continue to remember its coverage of famous athletes and its signature long-form journalism.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

