INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'InsideFlyer', N'insideflyer', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription A defunct monthly magazine catering to frequent flyers and loyalty program enthusiasts, providing information on travel rewards programs, aviation, hospitality and insider tips for maximizing miles and points.}}' + CHAR(13) + CHAR(10) + N'## Introduction' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InsideFlyer was a monthly magazine targeting frequent flyers and loyalty program members. The magazine provided readers with information on travel rewards programs, loyalty points, and the aviation and hospitality industries. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## History' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InsideFlyer was founded in 1986 by Randy Petersen, who is also the founder of FlyerTalk, a popular online forum for frequent flyers. The magazine was published by Frequent Flyer Services, Inc., which also published other titles such as Mileage Converter and Discount Travel. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Over the years, InsideFlyer expanded its coverage to include not only frequent flyer programs but also other travel reward programs, such as hotel loyalty programs. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In 2006, InsideFlyer was acquired by the Colorado-based Engle Group, a marketing company that specializes in travel and hospitality. The magazine continued to publish but later ceased operations in 2013 due to declining readership and the shift towards online media. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Content ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InsideFlyer focused on providing readers with information on travel rewards programs, loyalty points, and the aviation and hospitality industries. The magazine covered topics such as the latest promotions, changes to loyalty programs, and insider tips for maximizing miles and points. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In addition to its travel rewards coverage, InsideFlyer also included destination guides, travel news, and feature stories on travel-related topics. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Audience and Reception' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'InsideFlyer had a dedicated readership among frequent flyers and loyalty program members. The magazine was known for its expertise in travel rewards and its insider tips for getting the most out of loyalty programs. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine won several awards throughout its publication history, including the Lowell Thomas Award for excellence in travel journalism. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'## Legacy' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Despite its relatively short publication history, InsideFlyer is remembered as an influential magazine that helped shape the landscape of frequent flyer programs and loyalty points. Its editorial stance on miles and points maximization continues to influence and help travelers today.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

