INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'The Magazine of American History', N'the-magazine-of-american-history', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription Historical magazine covering a broad range of topics related to American history and culture with articles written by notable historians and authors.}}' + CHAR(13) + CHAR(10) + N'The Magazine of American History is a historical magazine that focused on a broad range of topics related to American history and culture, including politics, society, and notable figures. It was founded in 1877 by Martha Joanna Lamb and is published by the Historical Publication Company.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine features a mix of original articles, archival materials, and book reviews, covering topics such as the American Revolution, the Civil War, and the suffrage movement. It offers a unique perspective on American history, serving as a historical resource for scholars, researchers, and history enthusiasts alike.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Over the years, the magazine has published numerous notable pieces, including articles written by noted historians and authors such as Henry Cabot Lodge, Henry Adams, and Theodore Roosevelt. It has also included first-hand accounts written by figures such as George Washington and Abraham Lincoln.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, The Magazine of American History continues to serve as a respected historical publication, providing in-depth coverage of American history and culture. Its archives and articles offer valuable insight into the events and people that have shaped the United States over the centuries.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Magazine of Western History
INSERT Articles (Title, UrlSlug, SiteId, Culture)
VALUES ( N'Magazine of Western History', N'magazineof-western-history', 1, 'en' );
INSERT ArticleRevisions (ArticleId, [Text], RevisionReason, CreatedByAspNetUserId)
VALUES (SCOPE_IDENTITY(), N'{{ShortDescription Publication covering the history and culture of the American West, featuring articles, historic photos, and illustrations by prominent writers and artists.}}' + CHAR(13) + CHAR(10) + N'Magazine of Western History was a historical magazine that focused on a broad range of topics related to the history and culture of the American West. It was founded in 1884 by Moses K. Armstrong and published by the Western Reserve Historical Society.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The magazine featured a mix of articles, historic photographs, and illustrations, covering topics such as exploration, pioneer life, mining, and the development of the western United States. It offered a unique perspective on the history of the American West, serving as a historical resource for scholars, researchers, and history enthusiasts.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'The Magazine of Western History was published until 1917, with over 30 volumes produced during its publication run. Notable contributions came from writers such as Theodore Roosevelt, Lewis and Clark historian Reuben Gold Thwaites, and western artist Frederic Remington. ' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'In 1918, the magazine was merged with "The Mississippi Valley Historical Review" and renamed the "Mississippi Valley Historical Review with The Western Historical Quarterly". In 1997, "The Mississippi Valley Historical Review" would later be renamed the Journal of American History.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10) + N'Today, the Magazine of Western History serves as a valuable historical resource for those interested in the history and culture of the American West during the late 19th and early 20th centuries. Its archives offer a glimpse into the events and figures that shaped this pivotal period in American history.{{Categories Magazines}}', N'Article created.', '7240be61-df81-46f9-8152-6a48b96abc40');

-- Magic
